-- ----------------------------
-- Table structure for article
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `sid` int(11) NOT NULL AUTO_INCREMENT,
  `site_sid` int(11) DEFAULT NULL,
  `location_sid` int(11) DEFAULT NULL,
  `topic_sid` int(11) DEFAULT '0',
  `url` varchar(255) DEFAULT NULL,
  `collect_time` bigint(13) DEFAULT NULL,
  `title` varchar(255) DEFAULT NULL,
  `content` text,
  `publish_time` bigint(13) DEFAULT NULL,
  `processed` tinyint(1) DEFAULT '0',
  `processed_tp` tinyint(1) DEFAULT '0',
  `processed_lc` tinyint(1) DEFAULT '0',
  `abstract` text,
  `keywords` varchar(255) DEFAULT NULL,
  `author` varchar(45) DEFAULT NULL,
  PRIMARY KEY (`sid`),
  KEY `article_location` (`location_sid`),
  KEY `artile_topic` (`topic_sid`),
  KEY `article_site` (`site_sid`)
) ENGINE=InnoDB AUTO_INCREMENT=1458 DEFAULT CHARSET=utf8;

