INSERT INTO `article` VALUES ('1124', '2', null, '0', 'http://news.china.com/focus/nhctsj/11101498/20110721/16662000.html', '1399706934464', '杨洁篪会见越南外长谈南海问题:避免争议再次升温', '<script src=\"http://cast.ra.icast.cn/p/?id=2084\"></script>\r\n<script>\r\nvar timestamp = Date.parse(new Date());\r\nvar src = \"http://dvsend.china.com/cc/00M98adcrm?v=\"+timestamp;\r\nvar s = document.createElement(\"SCRIPT\");\r\n	document.getElementsByTagName(\"HEAD\")[0].appendChild(s);\r\n	s.src = src;\r\n</script>\r\n<script>\r\nvar timestamp = Date.parse(new Date());\r\nvar src = \"http://dvsend.china.com/cc/00M98adcrm?v=\"+timestamp;\r\nvar s = document.createElement(\"SCRIPT\");\r\ndocument.getElementsByTagName(\"HEAD\")[0].appendChild(s);\r\ns.src = src;\r\n</script><script type=\"text/javascript\">\r\ndocument.write(\'<a style=\"display:none!important\" id=\"tanx-a-mm_13181159_1847728_13728914\"></a>\');\r\ntanx_s = document.createElement(\"script\");\r\ntanx_s.type = \"text/javascript\";\r\ntanx_s.charset = \"gbk\";\r\ntanx_s.id = \"tanx-s-mm_13181159_1847728_13728914\";\r\ntanx_s.async = true;\r\ntanx_s.src = \"http://p.tanx.com/ex?i=mm_13181159_1847728_13728914\";\r\ntanx_h = document.getElementsByTagName(\"head\")[0];\r\nif(tanx_h)tanx_h.insertBefore(tanx_s,tanx_h.firstChild);\r\n</script><img src=\"http://dvsend.china.com/cc/00NELhttp://dvs.china.com/385/dot.gif\" width=\"0\" height=\"0\" border=\"0\"><script type=\"text/javascript\">\r\ndocument.write(\'<a style=\"display:none!important\" id=\"tanx-a-mm_13181159_1847728_13728914\"></a>\');\r\ntanx_s = document.createElement(\"script\");\r\ntanx_s.type = \"text/javascript\";\r\ntanx_s.charset = \"gbk\";\r\ntanx_s.id = \"tanx-s-mm_13181159_1847728_13728914\";\r\ntanx_s.async = true;\r\ntanx_s.src = \"http://p.tanx.com/ex?i=mm_13181159_1847728_13728914\";\r\ntanx_h = document.getElementsByTagName(\"head\")[0];\r\nif(tanx_h)tanx_h.insertBefore(tanx_s,tanx_h.firstChild);\r\n</script><img src=\"http://dvsend.china.com/cc/00MZZhttp://dvs