INSERT INTO `article` VALUES ('1229', '2', null, '0', 'http://news.china.com/focus/nhctsj/11101498/20130205/17670402.html', '1399712617789', '美军舰频访菲 被指借南海主权争议寻求在菲驻军', '<script type=\"text/javascript\">\r\ndocument.write(\'<a style=\"display:none!important\" id=\"tanx-a-mm_13181159_1847728_13728914\"></a>\');\r\ntanx_s = document.createElement(\"script\");\r\ntanx_s.type = \"text/javascript\";\r\ntanx_s.charset = \"gbk\";\r\ntanx_s.id = \"tanx-s-mm_13181159_1847728_13728914\";\r\ntanx_s.async = true;\r\ntanx_s.src = \"http://p.tanx.com/ex?i=mm_13181159_1847728_13728914\";\r\ntanx_h = document.getElementsByTagName(\"head\")[0];\r\nif(tanx_h)tanx_h.insertBefore(tanx_s,tanx_h.firstChild);\r\n</script><img src=\"http://dvsend.china.com/cc/00NELhttp://dvs.china.com/385/dot.gif\" width=\"0\" height=\"0\" border=\"0\"><script type=\"text/javascript\">\r\ndocument.write(\'<a style=\"display:none!important\" id=\"tanx-a-mm_13181159_1847728_13728914\"></a>\');\r\ntanx_s = document.createElement(\"script\");\r\ntanx_s.type = \"text/javascript\";\r\ntanx_s.charset = \"gbk\";\r\ntanx_s.id = \"tanx-s-mm_13181159_1847728_13728914\";\r\ntanx_s.async = true;\r\ntanx_s.src = \"http://p.tanx.com/ex?i=mm_13181159_1847728_13728914\";\r\ntanx_h = document.getElementsByTagName(\"head\")[0];\r\nif(tanx_h)tanx_h.insertBefore(tanx_s,tanx_h.firstChild);\r\n</script><img src=\"http://dvsend.china.com/cc/00MZZhttp://dvs.china.com/385/dot.gif\" width=\"0\" height=\"0\" border=\"0\"><script type=\"text/javascript\">\r\ndocument.write(\'<a style=\"display:none!important\" id=\"tanx-a-mm_13181159_1847728_13728914\"></a>\');\r\ntanx_s = document.createElement(\"script\");\r\ntanx_s.type = \"text/javascript\";\r\ntanx_s.charset = \"gbk\";\r\ntanx_s.id = \"tanx-s-mm_13181159_1847728_13728914\";\r\ntanx_s.async = true;\r\ntanx_s.src = \"http://p.tanx.com/ex?i=mm_13181159_1847728_13728914\";\r\ntanx_h = document.getElementsByTagName(\"head\")[0];\r\nif(tanx_h)tanx_h.insertBefore(tanx_s,tanx_h.firstChild)