INSERT INTO `article` VALUES ('1276', '2', null, '0', 'http://news.china.com/zh_cn/hd/11127798/20131021/18101164.html', '1399715619180', '揭秘中南海保镖：眼神犀利 手提神秘黑包(组图)', '上一张下一张中南海保镖,是国家领导人身边的警卫员，民间戏称“大内高手”。现实中的中南海保镖,受过严格训练的中国特种部队成员，身手高强，思维敏捷。', '1382338902000', '0', '0', '0', null, null, null);
