INSERT INTO `article` VALUES ('1280', '2', null, '0', 'http://news.china.com/zh_cn/hd/11127798/20140118/18296770.html', '1399717250894', '\"航母style\"换新姿势 辽宁舰南海巡航原始画面曝光', '上一张下一张中国网1月17日讯 近日，网友曝光了我辽宁号航母去年12月在南海巡航的原始画面。甲板，驾驶室等画面以及直升机和歼击机的起降过程都被曝光。引人注意的是，“航母style”换了新姿势，新版“航母style”中，指挥员右膝跪地，左手高举，喜感不输当年。', '1390008264000', '0', '0', '0', null, null, null);
